  getDeparments() {
    this.deparmentService.getAll().subscribe(res => {
      //console.log(res);
      this.departments = res.data;
    });
  }
  getMunicipalitiesByDeparment(event) {
    let departmentId = event.target.value;
    this.communities = [];
    this.municipalities = [];
    this.departmentId = Number(departmentId);
    this.municipalitiesService.getByDepartmentId(departmentId).subscribe(res => {
      this.municipalities = res.data;
    });
  }
  getMunicipalities(event) {
    let municipalityId = event.target.value;
    this.municipalityId = Number(municipalityId);
    this.communitiesService.getByCommunityByMunicipalityId(municipalityId).subscribe(res => {
      this.communities = res.data;
    });
  }
  getQuestions() {
    this.questionsService.getAll().subscribe(res => {
      this.question = res.data;
      let data = [];
      let group = {};
      res.data.forEach(item => {
        data.push({
          questionId: item.id,
          descripcion: item.description,
          otherResponse: "",
          recommendation: item.recommendation,
          key: "questions?" + item.id,
          key2: "recommendations?" + item.id
        });
      });
